import { fingerIsMoving } from "./../../app.js";


describe("fingerIsMoving() tests", () => {
  test("returns true if finger is moving (1)", () => {
    const finger = {
      tipVelocity: [27.1, 15, 18]
    };
    const result = fingerIsMoving(finger);

    expect(result).toEqual(true);
  });

  test("returns true if finger is moving (2)", () => {
    const finger = {
      tipVelocity: [2, 27, 18]
    };
    const result = fingerIsMoving(finger);

    expect(result).toEqual(true);
  });

  test("returns false if finger is not moving", () => {
    const finger = {
      tipVelocity: [8, 15, 26.9]
    };
    const result = fingerIsMoving(finger);

    expect(result).toEqual(false);
  });
});
