import { frameNotValid } from "./../../app.js";


describe("frameNotValid", () => {
  const fingerInfo = { someInfo: "1" };

  describe("Leap sees 2 hands", () => {
    test("returns true if one hand has 0 fingers and another hand has 10 fingers", () => {
      const frame = {
        hands: [
          {
            notRelevantInfo: {},
            fingers: []
          },
          {
            notRelevantInfo: {},
            fingers: [
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo
            ]
          }
        ],
        otherNotRelevantInfo: {}
      };

      expect(frameNotValid(frame)).toBe(true);
    });

    test("returns false if both hands have 5 fingers", () => {
      const frame = {
        hands: [
          {
            notRelevantInfo: {},
            fingers: [
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo
            ]
          },
          {
            notRelevantInfo: {},
            fingers: [
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo
            ]
          }
        ],
        otherNotRelevantInfo: {}
      };

      expect(frameNotValid(frame)).toBe(false);
    });

    test("returns false if one of the hands has more than 5 fingers", () => {
      const frame = {
        hands: [
          {
            notRelevantInfo: {},
            fingers: [
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo
            ]
          },
          {
            notRelevantInfo: {},
            fingers: [
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo
            ]
          }
        ],
        otherNotRelevantInfo: {}
      };

      expect(frameNotValid(frame)).toBe(false);
    });
  });

  describe("Leap sees 1 hand", () => {
    test("returns false if hand has 5 fingers", () => {
      const frame = {
        hands: [
          {
            notRelevantInfo: {},
            fingers: [
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo
            ]
          }
        ],
        otherNotRelevantInfo: {}
      };

      expect(frameNotValid(frame)).toBe(false);
    });

    test("returns false if hand has less than 5 fingers", () => {
      const frame = {
        hands: [
          {
            notRelevantInfo: {},
            fingers: [fingerInfo, fingerInfo, fingerInfo, fingerInfo]
          }
        ],
        otherNotRelevantInfo: {}
      };

      expect(frameNotValid(frame)).toBe(false);
    });

    test("returns false if hand has more than 5 fingers", () => {
      const frame = {
        hands: [
          {
            notRelevantInfo: {},
            fingers: [
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo,
              fingerInfo
            ]
          }
        ],
        otherNotRelevantInfo: {}
      };

      expect(frameNotValid(frame)).toBe(false);
    });
  });
});
