import { gestureHasEnoughFrames } from "./../../app.js";


describe("gestureHasEnoughFrames() tests", () => {
  test("returns true if gesture has enough frames (1)", () => {
    const result = gestureHasEnoughFrames(18);

    expect(result).toEqual(true);
  });

  test("returns true if gesture has enough frames (2)", () => {
    const result = gestureHasEnoughFrames(17);

    expect(result).toEqual(true);
  });

  test("returns false if gesture does not have enough frames", () => {
    const result = gestureHasEnoughFrames(16);

    expect(result).toEqual(false);
  });
});
