import { palmMoving } from "./../../app.js";


describe("palmMoving() tests", () => {
  test("returns true if palm is moving (1)", () => {
    const palmVelocity = [20, 15, 18];
    const result = palmMoving(palmVelocity);

    expect(result).toEqual(true);
  });

  test("returns true if palm is moving (2)", () => {
    const palmVelocity = [2, 20.1, 18];
    const result = palmMoving(palmVelocity);

    expect(result).toEqual(true);
  });

  test("returns false if palm is not moving", () => {
    const palmVelocity = [8, 15, 19.9];
    const result = palmMoving(palmVelocity);

    expect(result).toEqual(false);
  });
});
