import { prepareFrame } from "./../../app.js";


test("prepareFrame", () => {
  const frame = {
    hands: [
      {
        palmPosition: [25, 12, 14],
        fingers: [
          { dipPosition: [1, 5, 58] },
          { dipPosition: [10, 15, 51] },
          { dipPosition: [11, 8, 61] },
          { dipPosition: [15, 1, 85] },
          { dipPosition: [8, 5, 4] },
        ]
      }
    ]
  };
  frame.hands[0].pitch = jest.fn();
  frame.hands[0].pitch.mockReturnValue(1);

  frame.hands[0].roll = jest.fn();
  frame.hands[0].roll.mockReturnValue(2);

  frame.hands[0].yaw = jest.fn();
  frame.hands[0].yaw.mockReturnValue(3);
  
  
  const result = prepareFrame(frame);
  expect(result).toEqual([
    25, 12, 14,
    57.29577951308232,
    114.59155902616465,
    171.88733853924697,
    1, 5, 58,
    10, 15, 51,
    11, 8, 61,
    15, 1, 85,
    8, 5, 4
  ]);
});
